import classes from './catalog.module.scss';
import CatalogCard from '../catalog-card/catalog-card.jsx';
import Skeleton from '../catalog-card/skeleton.jsx';
import React from 'react';
import cards from '../../data/сategories.json';

const Catalog = () => {
    const [itemsCat, setItemsCat] = React.useState([]);
    const [isLoading, setIsLoading] = React.useState(true);

    React.useEffect(() => {
        setItemsCat(cards.slice(0, 6));
        setIsLoading(false);
    }, []);

    const loadMore = (e) => {
        let maxCount = cards.length;
        let countCards;
        if (itemsCat.length === maxCount) {
            setItemsCat(cards.slice(0, 6));
            e.target.textContent = 'Load more';
        } else {
            countCards = itemsCat.length + 6;
            if (countCards >= maxCount) {
                setItemsCat(cards);
                e.target.textContent = 'Hide';
            } else {
                setItemsCat(cards.slice(0, countCards));
            }
        }
    };

    return (
        <div className={classes.catalog}>
            <div className={classes.catalog__container}>
                <div className={classes.header}>
                    <h2 className={classes.title}>Categories</h2>
                    <a href='#' className={classes.more}>more categories</a>
                </div>
                <div className={classes.list}>
                    {itemsCat.map((card) => (
                        isLoading ? <Skeleton key={card.title} /> : (
                            <CatalogCard
                                key={card.title}
                                title={card.title}
                                image={card.image}
                                about={card.about}
                            />
                        )
                    ))}
                </div>
                <button onClick={(e) => {loadMore(e)}} className={`${classes.moreBtn} ${classes.more}`}>Load more</button>
                <a href='#' className={`${classes.more} ${classes._main}`}>more categories</a>
            </div>
        </div>
    );
};

export default Catalog;
